CREATE TABLE permit_holder(
   operator_id       INTEGER  NOT NULL PRIMARY KEY 
  ,organization_name VARCHAR(48) NOT NULL
  ,phone_number      VARCHAR(13) NOT NULL
  ,address_line_1    VARCHAR(35) NOT NULL
